import React, { Component } from 'react';
import Comment from './Comment';

class Comments extends Component {

    render() {

        const { comments } = this.props;
        const listComments = comments.comments.map((comment, index => {
            return(
                <Comment
                    key={index} 
                    comment={comment} 
                 />
                ) 
            }
        ))
        return(
            <div>
                <li>
                    {listComments}
                </li>
            </div>
        )
    }
}

export default Comments;
