            <li>Genre: {this.props.song.genre} </li>

            <button onClick = {this.IncrementVote}>Vote</button>{this.state.click}
            <li>Comment: {this.props.song.comments}</li>
          <br ></br>

          <form onSubmit={this.CreateComment} className="commentForm">
          <input 
          type="text"
          id="commentInput"
          placeholder="comment"/>
