import React, { Component } from 'react';
import Songs from './components/songs/Songs';
import SongInput from './components/songs/SongInput'
import SongService from './services/SongService';
import Comments from './components/comments/Comments';
import CommentInput from './components/comments/CommentInput';
import CommentsContainer from './containers/CommentsContainer'
import SongsContainer from './containers/SongsContainer'
import NavBar from './components/NavBar';
import { connect } from 'react-redux';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import './App.css';


class App extends Component {
  constructor() {
    super()
    this.state = {
      songs: [],
      comment: []
    }
  }

  componentDidMount() {
    SongService.fetchSongs()
    .then(songs => 
      this.setState({songs}))
  }

  addSong = song => { 
    SongService.createSong(song)
    .then(song => 
      this.setState({ 
        songs: this.state.songs.concat(song)[0] 
    }))
  }

  render() {
    return(
      <div className="app">
      <h1>Albums App</h1>
      <div className="AppPage">
        <CommentsContainer />
        <SongsContainer />
        <Comments />

        </div>

      </div>

    )
  }
} 

  const mapStateToProps = state => {
    return { songs: state.songs,
             comments: state.comment }
  }

const Nav = (props) => {
  return(
    <Router>
      <div className="app">
        <NavBar />
        <Route exact path="/comments" component={Comments} />
        <Route exact path="/comment_input" component={CommentInput} />
        <Route exact path="/song_input" component={SongInput} />
        <Route exact path="/songs" component={Songs} />

      </div>
    </Router>
  )
}

export default connect(mapStateToProps)(App);
