export function fetchSongs() {
    return (dispatch) => {
        dispatch({ type: 'ADDING_SONGS_REQUEST' });
            return fetch('https://localhost:3001/api/v1/songs')
            .then(response => response.json())
            .then(songs => dispatch({ type: 'LOAD_SONGS', songs}))
}}
