import React, { Component } from 'react';
import Comment from './Comment';

class Comments extends Component {

    render() {
        const { comment } = this.props;
        const listComments = this.props.comments.map((comment, index => {
            return(
                <p>
                    key={index} 
                    comment={comment} 
                </p>
            )
        }))

        return (
            <div>
                {listComments}
            </div>
        )
    }
}

export default Comments;


