    super()
    this.state = {
      songs: [],
      comment: []
    }
  }

  componentDidMount() {
    SongService.fetchSongs()
    .then(songs => 
      this.setState({songs}))
  }

  addSong = song => { 
    SongService.createSong(song)
    .then(song => 
      this.setState({ 
        songs: this.state.songs.concat(song)[0] 
    }))
  }

  render() {
    return(
      <div className="App">
      <h1>Albums App</h1>

