export const fetchSongs = () => {
    let songs;
    return (dispatch) => {
      dispatch({ type: 'LOADING_SONGS' });
      return fetch('http://localhost:3000/api/v1/songs')
        .then(response => response.json())
        .then(responseJSON => {
          songs = responseJSON.songs.map(song => ({ url: song.url }))
          dispatch({
            type: 'FETCH_SONGS',
            payload: songs
          })
        });
    };
  }

export const loadingSongs = () => {
    return {
        type: 'LOADING_SONGS'
    }
}


