        .then(response => response.json())
        .then(responseJSON => {
            songsList = responseJSON.songs.map(songs => ({ song }))
            dispatchEvent({
                type: 'FETCH_SONGS',

            })
        })
    },

    createSong(song) {
        const fetchilizer = {
        method: 'POST',
        body: JSON.stringify({
            song: song
        }),
        headers: {
        'Content-Type': 'application/json'
        }
    }
    return fetch(`${API_URL}/api/vi`, fetchilizer) 
    .then(response  => response.json())
    }   
}


export default SongService;
