import React, {
  Component
} from 'react';


class Song extends Component {
  constructor() {
    super()
    this.state = {

      comments: [],
      show: true
    }
  }

  IncrementVote = () => {
    this.setState((prevState) => {
      return {
        click: prevState.click + 1
      }
    })
  }




  CreateComment = (event) => {
    event.preventDefault()
    let comment = document.getElementById("commentInput")
    this.setState((prevState) => {
      return {
        comments: [...prevState.comments, comment.value]
      }
    })

  }
  render() {
      console.log(this.state.comments)
      return (

        <div >
        <
        li > Title: {
          this.props.song.title
        } < /li> <
        li > Artist: {
          this.props.song.artist
        } < /li> <
        li > Genre: {
          this.props.song.genre
        } < /li>

        <button onClick = {
          this.IncrementVote
        } > Vote < /button>{this.state.click} <
        li > Comment: {
          this.props.song.comments
        } < /li> <
        br > < /br>

        <
        form onSubmit = {
          this.CreateComment
        }
        className = "commentForm" >
        <
        input type = "text"
        id = "commentInput"
        placeholder = "comment" / >
        <
        input type = "submit" > < /input> <
        /form>

        <
        ul > {
          this.state.comments.map((comment) => < li > {
              comment
            } < /li>)}


            <
            /ul> <
            /div>
          );
        }
      };

      export default Song;
