export default function songsReducer( state = { loading: false, 
    songs: [] }, action ) {

    switch (action.type) {

    case 'LOAD_SONGS':
        return action.songs

    case 'FETCH_SONGS': 
        return { loading: false, songs: [...action.payload ] };

    case "ADD_SONG":
        return [...state, action.song ];

    default:
        return state;
    };
};
