import React, { Component } from 'react';

class Comment extends Component {

    render() {
        return(
            <div className="organizedCommentList">

                "{this.props.comment.comment}"<br></br>
                <br></br><button onClick={() => this.props.deleteSong(this.props.song.id)}>Remove</button>

            </div>
        );
    }
};

export default Comment;
