function songsReducer( state = { loading: false, songs: [] }, action ) {
    let idx;

    switch (action.type) {

    case 'LOAD_SONGS':
        return {...state, loading: true };

    case 'FETCH_SONGS': 
        return { loading: false, songs: [...action.payload ] };

    case "ADD_SONG":
        return [...state, action.song ];

    case "DELETE_SONG":
        idx = state.indexOf(action.id);
        return [ ...state.slice(0, idx), ...state.slice(idx +1 )];

    default:
        return state;
    };
};

function commentsReducer( state = { loading: false, comments: [] }, action ){
    let idx;

    switch(action.type) {

    case 'LOAD_COMMENTS':
        return {...state, loading: true};

    case 'FETCH_COMMENTS': 
        return { loading: false, comment: [ ...action.payload ] };

    case "ADD_COMMENT":
        return [ ...state, action.comment ];

    case "REMOVE_COMMENT":
        idx = state.indexOf(action.id)
        return [ ...state.slice(0, idx), ...state.slice( idx + 1) ];

    default: 
        return state;
    };
};

export default rootReducer;
