import React, { Component } from 'react';
import Comment from './Comment';

class Comments extends Component {
    render() {
        const { comments } = this.props;
        const listComments = comments.comments.map(comment => {
            return(
                <li key={comment} >
                    <Comment

                    comment={comment} 
                    />
                </li>
                ) 
            }
        )

        return(
                <ul>
                    {listComments}
                </ul>

        )
    }
}

export default Comments;
