import React, { Component } from 'react';
import Home from './Home'
import CommentInput from './components/comments/CommentInput';
import Songs from './components/songs/Songs';
import SongInput from './components/songs/SongInput'
import SongService from './services/SongService';
import NavBar from './components/NavBar';
import { connect } from 'react-redux';
import {
  BrowserRouter as Router,
  Route, Link
} from 'react-router-dom';
import './App.css';


class App extends Component {
  constructor() {
    super()
    this.state = {
      songs: [],
      comment: []
    }
  }

  componentDidMount() {
    SongService.fetchSongs()
    .then(songs => 
      this.setState({songs}))
  }

  addSong = song => { 
    SongService.createSong(song)
    .then(song => 
      this.setState({ 
        songs: this.state.songs.concat(song)[0] 
    }))
  }

  render() {
    return(
      <div className="app">
      <h1>Albums App</h1>

      <div className="navbar">
        <h2>nav bar</h2>
        <CommentInput addComment={this.addComment}/>
      </div>

      <div className="sidebar">
        <p>side bar</p>
        <Nav/>
      </div>



      <div className="main-content">
        <p>content</p>
        <SongInput addSong={this.addSong}/>
      </div>
    </div>
    )
  }
} 

  const mapStateToProps = state => {
    return { songs: state.songs,
             comments: state.comment }
  }
const Nav = (props) => {
  return(
    <Router>
      <div className="app">
        <NavBar />
        <Route exact path="/" component={Home} />
        <Route exact path="/comment_input" component={CommentInput} />
        <Route exact path="/song_input" component={SongInput} />
        <Route exact path="/songs" component={Songs} />
      </div>
    </Router>
  )
}

export default connect(mapStateToProps)(App);
