export const fetchComments = () => {
    let comments;
    return (dispatch) => {
      dispatch({ type: 'LOADING_SONGS' });
      return fetch('http://localhost:3000/api/v1/comments')
        .then(response => response.json())
        .then(responseJSON => {
          dispatch({
            type: 'FETCH_COMMENTS',
            payload: responseJSON
          })
       });
    };
  }

export const loadingComments = () => {
    return {
        type: 'LOADING_COMMENTS'
    }
}
